/*!50003 DROP PROCEDURE IF EXISTS  `spShowData` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `spShowData`()
BEGIN
	DECLARE done INT DEFAULT 0;
	DECLARE a bigint(20);
	DECLARE b varchar(1000);
	DECLARE cur1 CURSOR FOR SELECT tid,tcontent FROM thought_master;
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;
	OPEN cur1;
	  REPEAT
	    FETCH cur1 INTO a, b;
	    if not done then 	    	    
	    select a,b;	    
	    end  if;
	  UNTIL done END REPEAT;
	  CLOSE cur1;
    END */$$
DELIMITER ;

